// Create a new XMLHttpRequest object
var xhr = new XMLHttpRequest();

// Configure it: GET-request for the URL of the HTML file
xhr.open('GET', 'gigs.html', true);

xhr.onreadystatechange = function () {
  // Check if the request is complete and was successful
  if (this.readyState === 4 && this.status === 200) {
    // Insert the HTML file content into the chosen container
    document.getElementById('includedContent').innerHTML = this.responseText;
  }
};

// Send the request
xhr.send();